mapper.select:
select CODE, CODE_NM 
from lettccmmndetailcode 
where code_id = #{code_id}

mapper.update:
update lettnemplyrinfo set ORGNZT_ID=#{ORGNZT_ID}, USER_NM=#{USER_NM},
PASSWORD=#{PASSWORD},
EMPL_NO=#{EMPL_NO}, IHIDNUM=#{IHIDNUM}, SEXDSTN_CODE=#{SEXDSTN_CODE}, BRTHDY=#{BRTHDY}, FXNUM=#{FXNUM}, HOUSE_ADRES=#{HOUSE_ADRES}, PASSWORD_HINT=#{PASSWORD_HINT}, PASSWORD_CNSR=#{PASSWORD_CNSR}, HOUSE_END_TELNO=#{HOUSE_END_TELNO}, AREA_NO=#{AREA_NO}, DETAIL_ADRES=#{DETAIL_ADRES}, ZIP=#{ZIP}, OFFM_TELNO=#{OFFM_TELNO}, MBTLNUM=#{MBTLNUM}, EMAIL_ADRES=#{EMAIL_ADRES}, OFCPS_NM=#{OFCPS_NM}, HOUSE_MIDDLE_TELNO=#{HOUSE_MIDDLE_TELNO}, GROUP_ID=#{GROUP_ID}, PSTINST_CODE=#{PSTINST_CODE}, EMPLYR_STTUS_CODE=#{EMPLYR_STTUS_CODE}, ESNTL_ID=#{ESNTL_ID}, CRTFC_DN_VALUE=#{CRTFC_DN_VALUE}
where EMPLYR_ID=#{EMPLYR_ID}
